CREATE TABLE "dev"."main"."q18" AS SELECT c_name, c_custkey, o_orderkey, o_orderdate, o_totalprice, SUM(l_quantity) FROM "dev"."tpch"."customer", "dev"."tpch"."orders", "dev"."tpch"."lineitem" WHERE o_orderkey IN (SELECT l_orderkey FROM "dev"."tpch"."lineitem" GROUP BY l_orderkey HAVING SUM(l_quantity) > 300) AND c_custkey = o_custkey AND o_orderkey = l_orderkey GROUP BY c_name, c_custkey, o_orderkey, o_orderdate, o_totalprice ORDER BY o_totalprice DESC, o_orderdate NULLS FIRST LIMIT 100